function FarmOfPig()
	local farm=FindUnitOfType(FourCC('npgf')) --свинья хрюшка
	local x,y=GetUnitXY(farm)
	local mf=0
	TimerStart(CreateTimer(), 60, true, function()
		local new=CreateUnit(Player(5),FourCC('n007'),x,y,180)
		mf=mf+1
		local hp=200+mf
		BlzSetUnitMaxHP(new,hp)
		HealUnit(new,hp)
	end)
end